INSERT INTO `hrec_ceb` (`name`,`id`,`style`,`unit`,`email`,`language`,`pred`,`book`,`auth`,`publish`,`version`,`year`,`isbn`,`price`,`date`,`profession`,`reason`,`situation`,`IntroUrl`,`duplicate`,`nbReason`,`empty`) VALUES ('曾馨瑩', '**', '教職員', '秘書室(約僱)', '**', '中文', '是', '只是不想將就在一起', '', '', NULL, NULL, '9789869028066', '', '2020-08-26 16:47:38', '', '揮別過去，我們都會更好的\r\n內容有散文和短篇小說兩種形式，散文的部分談戀愛、談擇偶、談性、談婚姻、談外遇、談離婚；短篇小說則以女主角怡君用第一人稱自述，為甚麼是「怡君」？因為這個名字是我們這個世代最多人取的女生名，她代表著我，也代表正在看這本書的妳。\r\n ', '已上架', 'https://www.books.com.tw/products/0010646641?sloc=main', '否', NULL, '否');
INSERT INTO `hrec_ceb` (`name`,`id`,`style`,`unit`,`email`,`language`,`pred`,`book`,`auth`,`publish`,`version`,`year`,`isbn`,`price`,`date`,`profession`,`reason`,`situation`,`IntroUrl`,`duplicate`,`nbReason`,`empty`) VALUES ('曾馨瑩', '**', '教職員', '秘書室(約僱)', '**', '中文', '是', '有你，更能做自己', '', '', NULL, NULL, '9789575035372', '', '2020-08-26 17:09:24', '', '\r\n\r\n\r\n\r\n　　好的親密關係不僅能支持彼此的人生，也能容納周圍的人事物。\r\n\r\n　　你必須了解自己心理深層的問題，也了解對方深層的問題，\r\n\r\n　　不斷克服內在的恐懼，超越慣性的防衛，\r\n\r\n　　面對真實，才能創造一段平衡穩定的關係。', '已上架', 'https://www.books.com.tw/products/0010848031?sloc=main', '否', NULL, '否');
INSERT INTO `hrec_ceb` (`name`,`id`,`style`,`unit`,`email`,`language`,`pred`,`book`,`auth`,`publish`,`version`,`year`,`isbn`,`price`,`date`,`profession`,`reason`,`situation`,`IntroUrl`,`duplicate`,`nbReason`,`empty`) VALUES ('曾馨瑩', '**', '教職員', '秘書室(約僱)', '**', '中文', '是', '學習。在一起的幸福', '', '', NULL, NULL, '9789862299777', '', '2020-08-26 17:10:10', '', '找回給予愛的能力，而非獲得愛的幸運\r\n這一次，我想嘗試的，不只是如何編織美麗迷惑的、讓你停留的網。\r\n而是，如何和你一起，將彼此從囚禁的掙扎中釋放，可以再飛。\r\n有時比翼，有時錯落。\r\n展開纏繞的網，成為我們之間，&#32220;長甜蜜的絲線。─', '已上架', 'https://www.books.com.tw/products/0010599625?sloc=main', '是', NULL, '否');
