INSERT INTO `hrec_ceb` (`name`,`id`,`style`,`unit`,`email`,`language`,`pred`,`book`,`auth`,`publish`,`version`,`year`,`isbn`,`price`,`date`,`profession`,`reason`,`situation`,`IntroUrl`,`duplicate`,`nbReason`,`empty`) VALUES ('田正儀', '**', '醫護人員', '大慶院區', '**', '中文', '是', '心智化：依附關係．情感調節．自我發展', '', '', NULL, NULL, '9789863572060', '', '2021-04-27 08:43:41', '', '全書共分三大部，第一部從哲學、心理學、行為科學、基因研究等領域，探索情感與心智化相關理論，以及基因與環境對發展的影響。第二部從發展角度，探討鏡映、表徵、精神等同、遊戲與假扮等概念，闡述人的自我和主體感如何在嬰幼兒到青春期的漫長過程中，一步步發展出來，並以兒童和青少年案例來說明。第三部則以臨床案例，將討論焦點帶到各種成年人心理病理的治療，特別是困難治療的個案如邊緣型人格障礙症患者等，結語則論及心智化在心理病理學以及心理治療上的綜合應用。', '已上架', 'https://www.books.com.tw/products/0010889420?loc=M_0005_002', '否', NULL, '否');
INSERT INTO `hrec_ceb` (`name`,`id`,`style`,`unit`,`email`,`language`,`pred`,`book`,`auth`,`publish`,`version`,`year`,`isbn`,`price`,`date`,`profession`,`reason`,`situation`,`IntroUrl`,`duplicate`,`nbReason`,`empty`) VALUES ('田正儀', '**', '醫護人員', '大慶院區', '**', '中文', '是', '接住墜落的青少年：我與那些受傷的孩子，及他們不安的家庭', '', '', NULL, NULL, '9789864062331', '', '2021-04-27 08:52:14', '', '家庭，是孩子最重要的人際支持系統。當孩子遇到困境，而人際系統卻失靈時，便無法給提供孩子穩定與支持的力量，甚至，還會是孩子痛苦與壓力的來源。\r\n\r\n當家庭人際系統失靈時，如果校園人際系統能夠及時啟動，某種程度，便能緩衝孩子持續受到的身心傷害，而學校輔導教師正是持續扮演這樣的角色。輔導教師穿針引線地讓孩子在學校系統中的人際支持更加完善，就像編織起一張網，牢牢地接住孩子。', '已上架', 'https://www.books.com.tw/products/0010888264?sloc=main', '否', NULL, '否');
